import { useState } from "react";
import TaskObj from './TaskObj.js'
export default function TodoList(params) {
    var [todoName, setTodoName] = useState("")
    var [todos, setTodos] = useState([])
    const addTodo = () =>{
        setTodos(...todos, new TaskObj(todoName))        
    }
    return(
        <div class = "container">
            <label for="add-todo" > Enter Todo Name: </label>
            <input type = "text" id = "add-todo" class = "add-todo" value = {todoName} onChange = {(e) => setTodoName(e.target.value)}>

            </input>
            <button onClick={() => addTodo()}> Add Todo</button>
            <div class = 'todos'>
                {todos.forEach((todo, index) =>{
                    <div>
                        todo.name
                    </div>
                })}
            </div>
        </div>
    );
}
