import React, { useState } from "react";

export default function Task(params) {
    var [state, setState] = useState(0)
    const updateState = () =>{
            if(state)
            var taskName = document.querySelector('.task-name')
            taskName.toggleClass('active')
    }
    return(
        <div class = "task">
            <input type = "checkbox" id = 'task-status' onChange = {updateState()}>
                <label for = 'task-status' id = 'task-name' className = 'task-name'>
                    {params.name}
                </label>
            </input>
        </div>
    );
}
